import React, { useState } from "react";
import { Form, Container, Button, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { postUsers } from "../helpers/users";
import Swal from "sweetalert2";

const Registro = () => {
  const [nombre, setNombre] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [password2, setPassword2] = useState("");
  const [img, setImg] = useState("");
  const navigate = useNavigate();
  const handleSubmit = (e) => {
    e.preventDefault();
    const usuario = {
      nombre,
      email,
      password,
      password2,
      img,
      rol: "USER_ROLE",
    };
    if (password && password2) {
      if (password === password2) {
        postUsers(usuario).then((respuesta) => {
          if (respuesta.errors) {
            return Swal.fire({
              title: respuesta.errors[0].msg,
              icon: "error",
              confirmButtonColor: "#3085d6",
            });
          }
          if (respuesta.msg) {
            window.alert(respuesta.msg);
            setTimeout(() => {
              navigate("../login", { replace: true });
            }, 1500);
          }
        });
      } else {
        return Swal.fire({
          title: "Las constraseÃ±as deben ser iguales",
          icon: "warning",
          cancelButtonColor: "#d33",
        });
      }
    } else {
      return Swal.fire({
        title: "Debe completar todos los campos",
        icon: "warning",
        cancelButtonColor: "#d33",
      });
    }
  };
  return (
    <Container className="background-up py-5 min-height">
      <div className="col d-flex justify-content-center ">
        <h1 className="my-5 text-white">REGISTRO</h1>
      </div>
      <Row className="my-5">
        <Form
          className="card-login col-8 col-lg-5 mx-auto mt-5 "
          onSubmit={(e) => handleSubmit(e)}
        >
          <Form.Label className="text-white">Nombre de Usuario:</Form.Label>
          <Form.Control
            value={nombre}
            onChange={(e) => setNombre(e.target.value)}
            type="text"
            maxLength={50}
          />

          <Form.Group className="mb-3 mt-3" controlId="formBasicEmail">
            <Form.Label className="text-white">EscribÃ­ tu mail:</Form.Label>
            <Form.Control
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              autoComplete="email"
              maxLength={50}
            />
          </Form.Group>

          <Form.Group
            className="mb-3 mx-auto text-white"
            controlId="formBasicPassword"
          >
            <Form.Label>ElegÃ­ una ContraseÃ±a:</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              maxLength={50}
            />
          </Form.Group>

          <Form.Group
            className="mb-3 mx-auto text-white"
            controlId="formBasicPassword"
          >
            <Form.Label>Confirma tu contraseÃ±a:</Form.Label>
            <Form.Control
              type="password"
              value={password2}
              onChange={(e) => setPassword2(e.target.value)}
              maxLength={50}
            />
          </Form.Group>

          <Form.Group className="mb-4 mx-auto text-dark">
            <Form.Label>Carga tu foto:</Form.Label>
            <Form.Control
              type="text"
              value={img}
              onChange={(e) => setImg(e.target.value)}
              maxLength={50}
            />
          </Form.Group>

          <Button
            variant="primary"
            type="submit"
            className="mb-3 btn btn-success rounded login-btn"
          >
            CREAR CUENTA
          </Button>
        </Form>
      </Row>
    </Container>
  );
};

export default Registro;
