import React, { useState } from "react";
import { Form, Container, Button, Row } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import { postUsers } from "../helpers/users";
import Swal from "sweetalert2";

const Registro = () => {
  const navigate = useNavigate();
  // const [nombre, setNombre] = useState("");
  // const [email, setEmail] = useState("");
  // const [password, setPassword] = useState("");
  // const [password2, setPassword2] = useState("");
  // const [img, setImg] = useState("");
  const [loading, setLoading] = useState(false);
  const [formValue, setFormValue] = useState({
    nombre: "",
    email: "",
    password: "",
    provincia: "",
    localidad: "",
    direccionEnvio: "",
    rol: "USER_ROLE",
  });
  const handleChange = (e) => {
    setFormValue({
      ...formValue,
      [e.target.name]: e.target.value,
    });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    if (formValue.password && formValue.password2) {
      if (formValue.password === formValue.password2) {
        postUsers(formValue).then((respuesta) => {
          if (respuesta.errors) {
            setLoading(false);
            return Swal.fire({
              title: respuesta.errors[0].msg,
              icon: "error",
              confirmButtonColor: "#3085d6",
            });
          }
          if (respuesta.msg) {
            window.alert(respuesta.msg);
            setTimeout(() => {
              navigate("../login", { replace: true });
            }, 1500);
          }
        });
      } else {
        return Swal.fire({
          title: "Las constraseñas deben ser iguales",
          icon: "error",
          cancelButtonColor: "#d33",
        });
      }
    } else {
      return Swal.fire({
        title: "Debe completar todos los campos",
        icon: "warning",
        cancelButtonColor: "#d33",
      });
    }
  };
  return (
    <Container fluid className="background-up py-5 min-height">
      <div className="col d-flex justify-content-center ">
        <h1 className="my-5 text-white">REGISTRO</h1>
      </div>
      <Row className="my-5">
        <Form
          className="card-login col-8 col-lg-5 mx-auto mt-5 "
          onSubmit={handleSubmit}
        >
          <Form.Label className="text-white">Nombre de Usuario:</Form.Label>
          <Form.Control
            name="nombre"
            value={formValue.nombre}
            onChange={handleChange}
            required
            type="text"
            maxLength={70}
          />

          <Form.Group className="mb-3 mt-3" controlId="formBasicEmail">
            <Form.Label className="text-white">Escribí tu mail:</Form.Label>
            <Form.Control
              name="email"
              type="email"
              value={formValue.email}
              required
              onChange={handleChange}
              autoComplete="email"
              maxLength={50}
            />
          </Form.Group>

          <Form.Group
            className="mb-3 mx-auto text-white"
            controlId="formBasicPassword"
          >
            <Form.Label>Contraseña:</Form.Label>
            <Form.Control
              name="password"
              type="password"
              value={formValue.password}
              required
              onChange={handleChange}
              maxLength={50}
            />
          </Form.Group>

          <Form.Group className="mb-3 mx-auto text-white">
            <Form.Label>Confirma tu contraseña:</Form.Label>
            <Form.Control
              name="password2"
              type="password"
              value={formValue.password2}
              required
              onChange={handleChange}
              maxLength={50}
            />
          </Form.Group>
          <Form.Label className="text-dark">Provincia:</Form.Label>
          <Form.Control
            name="provincia"
            value={formValue.provincia}
            onChange={handleChange}
            required
            type="text"
            maxLength={50}
          />
          <Form.Label className="text-dark">Localidad:</Form.Label>
          <Form.Control
            name="localidad"
            value={formValue.localidad}
            onChange={handleChange}
            required
            type="text"
            maxLength={50}
          />
          <Form.Label className="text-dark">Dirección:</Form.Label>
          <Form.Control
            name="direccionEnvio"
            value={formValue.direccionEnvio}
            onChange={handleChange}
            required
            type="text"
            maxLength={50}
          />

          <Form.Group className="mb-4 mx-auto text-dark">
            <Form.Label>Carga tu foto:</Form.Label>
            <Form.Control
              name="img"
              type="text"
              value={formValue.img}
              onChange={handleChange}
              maxLength={700}
            />
          </Form.Group>

          <Button
            variant="primary"
            type="submit"
            className="mb-3 btn btn-success rounded login-btn"
          >
            CREAR CUENTA
          </Button>
        </Form>
      </Row>
    </Container>
  );
};

export default Registro;
