import React, { useState, useEffect } from "react";
import { getProducts } from "../../../helpers/products";

const cerrarSesion = () => {
  localStorage.setItem("auth", JSON.stringify(""));
};

export const HulkNavbar = () => {
  const [products, setProducts] = useState([]);
  const [state, setState] = useState({ rol: "" });
  useEffect(() => {
    getProducts().then((respuesta) => {
      setProducts(respuesta.products);
    });
  }, []);
  useEffect(() => {
    const datos = JSON.parse(localStorage.getItem("auth")) || [];
    setState(datos.usuario);
  }, []);
  console.log(products);
  let comic = products.filter((producto) => {
    producto.categorie.nombre === "Accesorios";
  });
  console.log(comic);
  // const comics = (products) => {
  //
  //   console.log(comic);
  // };
  return (
    <nav class="navbar navbar-expand-lg navbar-light bg-light">
      <a class="navbar-brand" href="/">
        HulkStore
      </a>
      <button
        class="navbar-toggler"
        type="button"
        data-toggle="collapse"
        data-target="#navbarNavAltMarkup"
        aria-controls="navbarNavAltMarkup"
        aria-expanded="false"
        aria-label="Toggle navigation"
      >
        <span class="navbar-toggler-icon"></span>
      </button>
      <div class="collapse navbar-collapse" id="navbarNavAltMarkup">
        <div class="navbar-nav">
          <a class="nav-link active" href="/">
            {/* onClick={comics()} */}
            Comics
          </a>
          <a class="nav-link" href="/">
            Juguetes
          </a>
          <a class="nav-link" href="/">
            Accesorios
          </a>
          <a class="nav-link" href="/carrito">
            Carrito
          </a>
          {state.rol === "ADMIN_ROLE" ? (
            <a class="nav-link disable" href="/admin">
              Admin
            </a>
          ) : (
            ""
          )}
          <form class="form-inline my-2 my-lg-0 ">
            <button
              onClick={cerrarSesion}
              class="btn btn-outline-success my-2 my-sm-0"
              type="submit"
            >
              Cerrar Sesi√≥n
            </button>
          </form>
        </div>
      </div>
    </nav>
  );
};
