import React, { useState, useEffect } from "react";
import { Modal, Button } from "react-bootstrap";
import { getProduct, postProducts, putProducts } from "../../helpers/products";
import { getCategories } from "../../helpers/categories";

const ModalProducts = ({ show, handleClose, actualizar }) => {
  const [loading, setLoading] = useState(false);
  const [categories, setCategories] = useState([]);
  const [formValue, setFormValue] = useState({
    nombre: "",
    precio: "",
    descripcion: "",
    categorie: "",
    disponible: true,
    stock: "",
  });

  useEffect(() => {
    getCategories().then((respuesta) => {
      setCategories(respuesta.categories);
    });
  }, []);

  useEffect(() => {
    setFormValue({
      nombre: "",
      precio: "",
      descripcion: "",
      categorie: "",
      disponible: true,
      stock: "",
    });

    if (actualizar) {
      getProduct(actualizar).then((respuesta) => {
        setFormValue({
          cantidad: 0,
          nombre: respuesta.product.nombre,
          precio: respuesta.product.precio,
          descripcion: respuesta.product.descripcion,
          categorie: respuesta.product.categorie,
          disponible: respuesta.product.disponible,
          stock: respuesta.product.stock,
        });
      });
    }
  }, [actualizar]);

  const handleChange = ({ target }) => {
    if (target.name === "disponible") {
      setFormValue({
        ...formValue,
        [target.name]: target.checked,
      });
    } else {
      setFormValue({
        ...formValue,
        [target.name]: target.value,
      });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    setLoading(true);

    if (actualizar) {
      putProducts(actualizar, formValue).then((respuesta) => {
        if (respuesta.errors) {
          setLoading(false);
          return window.alert(respuesta.errors[0].msg);
        }
        if (respuesta.msg) {
          window.alert(respuesta.msg);
        }
        setLoading(false);
        setFormValue({
          nombre: "",
          precio: "",
          descripcion: "",
          categorie: "",
          disponible: true,
          stock: "",
        });
        handleClose();
      });
    } else {
      postProducts(formValue).then((respuesta) => {
        if (respuesta.errors) {
          setLoading(false);
          return window.alert(respuesta.errors[0].msg);
        }
        if (respuesta.msg) {
          window.alert(respuesta.msg);
        }
        setLoading(false);
        setFormValue({
          nombre: "",
          precio: "",
          descripcion: "",
          categorie: "",
          disponible: true,
          stock: "",
        });

        handleClose();
      });
    }
  };
  return (
    <div>
      <Modal show={show} onHide={handleClose} centered>
        <Modal.Header closeButton>
          <Modal.Title>
            {actualizar ? "Modificar producto" : "Nuevo producto"}
          </Modal.Title>
        </Modal.Header>
        <form onSubmit={handleSubmit}>
          <Modal.Body>
            <div className="form-group">
              <label>Nombre</label>
              <input
                type="text"
                name="nombre"
                className="form-control"
                placeholder="Ej: Hulk: La fuerza verde."
                required
                value={formValue.nombre}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Precio</label>
              <input
                type="number"
                name="precio"
                className="form-control"
                value={formValue.precio}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Descripci√≥n</label>
              <textarea
                type="text"
                name="descripcion"
                className="form-control"
                value={formValue.descripcion}
                onChange={handleChange}
              />
            </div>
            <div className="form-group">
              <label>Categorias</label>
              <select
                className="form-select"
                name="categorie"
                value={formValue.categorie}
                onChange={handleChange}
                required
              >
                {categories.map((categorie) => (
                  <option key={categorie._id} value={categorie._id}>
                    {categorie.nombre}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                checked={formValue.disponible}
                value={formValue.disponible}
                onChange={handleChange}
                name="disponible"
              />
              <label>Disponible</label>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={handleClose}>
              Close
            </Button>
            <Button variant="success" type="submit" disabled={loading}>
              Save Changes
            </Button>
          </Modal.Footer>
        </form>
      </Modal>
    </div>
  );
};

export default ModalProducts;
