import React, { useState, useEffect } from "react";
import { getProducts } from "../helpers/products";
import { HulkNavbar } from "../components/common/navbar/HulkNavbar";
import CardProd from "../components/CardProd";

const Inicio = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    getProducts().then((respuesta) => {
      setProducts(respuesta.products);
    });
  }, []);

  const filtro = (categ) => {
    let prodFiltrado = products.filter(
      (product) => product.categorie.nombre === categ
    );
    getProducts().then((respuesta) => {
      setProducts(respuesta.products);
    });

    setProducts(prodFiltrado);
  };

  return (
    <>
      <HulkNavbar />
      <div class="navbar navbar-expand-lg navbar-dark bg-dark">
        <div>
          <div class="navbar-nav">
            <button class="nav-link active" onClick={() => filtro("Comics")}>
              Comics
            </button>
            <button class="nav-link" onClick={() => filtro("Juguetes")}>
              Juguetes
            </button>
            <button class="nav-link" onClick={() => filtro("Accesorios")}>
              Accesorios
            </button>
            <button class="nav-link" href="/carrito">
              Carrito
            </button>
          </div>
        </div>
      </div>
      <div className="container mb-3">
        <h1 className="mb-3">Elige tu producto</h1>
        <div className="d-flex justify-content-center my-3"></div>
        <CardProd products={products} />
      </div>
    </>
  );
};
export default Inicio;
